mapper.select:
select SQL_CALC_FOUND_ROWS cop.company_no , cop.company_id , cop.company_password , cop.compay_name , cop.business_no , cop.ceo_name , cop.postal_no , cop.postal_address , cop.detail_address , cop.insert_datetime , cop.update_datetime from company_info cop
and cop.compay_name like concat('%',#{compayName},'%')
and cop.ceo_name like concat('%',#{ceoName},'%')
order by ${srhOrderBy} ${srhOrderAsc}
limit ${startRow} , ${pageRow}



select 	count(*) count  
from 	company_info  
where company_id = #{companyId}

mapper.update:


	insert into company_info (  
		 company_no   
		, company_id   
		, company_password   
		, compay_name   
		, business_no   
		, ceo_name   
		, postal_no   
		, postal_address   
		, detail_address   
		, insert_datetime   
		, update_datetime   
 	) values (  
		 #{companyNo}  
		, #{companyId}  
		, #{companyPassword}  
		, #{compayName}  
		, #{businessNo}  
		, #{ceoName}  
		, #{postalNo}  
		, #{postalAddress}  
		, #{detailAddress}  
		, now()  
		, now()  
 	)

choose.when:


update company_info set  
	 update_datetime = now()  
where company_no = #{companyNo}

choose.otherwise:


update company_info set  
	 company_id = #{companyId}  
	, company_password = #{companyPassword}  
	, compay_name = #{compayName}  
	, business_no = #{businessNo}  
	, ceo_name = #{ceoName}  
	, postal_no = #{postalNo}  
	, postal_address = #{postalAddress}  
	, detail_address = #{detailAddress}  
	, update_datetime = now()  
where company_no = #{companyNo}
