mapper.select:
select SQL_CALC_FOUND_ROWS cus.customer_no , cus.company_no , cus.customer_id , cus.customer_password , cus.customer_name , cus.business_no , cus.ceo_name , cus.postal_no , cus.postal_address , cus.detail_address , cus.payment_day , cus.insert_datetime , cus.update_datetime from customer_info cus
and cus.customer_name like concat('%',#{customerName},'%')
and cus.business_no like concat('%',#{businessNo},'%')
order by ${srhOrderBy} ${srhOrderAsc}
limit ${startRow} , ${pageRow}



select 	count(*) count  
from 	customer_info  
where customer_id = #{customerId}

mapper.update:


	insert into customer_info (  
		 customer_no   
		, company_no   
		, customer_id   
		, customer_password   
		, customer_name   
		, business_no   
		, ceo_name   
		, postal_no   
		, postal_address   
		, detail_address   
		, payment_day   
		, insert_datetime   
		, update_datetime   
 	) values (  
		 #{customerNo}  
		, #{companyNo}  
		, #{customerId}  
		, #{customerPassword}  
		, #{customerName}  
		, #{businessNo}  
		, #{ceoName}  
		, #{postalNo}  
		, #{postalAddress}  
		, #{detailAddress}  
		, #{paymentDay}  
		, now()  
		, now()  
 	)

choose.when:


update customer_info set  
	 update_datetime = now()  
where customer_no = #{customerNo}

choose.otherwise:


update customer_info set  
	 company_no = #{companyNo}  
	, customer_id = #{customerId}  
	, customer_password = #{customerPassword}  
	, customer_name = #{customerName}  
	, business_no = #{businessNo}  
	, ceo_name = #{ceoName}  
	, postal_no = #{postalNo}  
	, postal_address = #{postalAddress}  
	, detail_address = #{detailAddress}  
	, payment_day = #{paymentDay}  
	, update_datetime = now()  
where customer_no = #{customerNo}
