mapper.select:
select SQL_CALC_FOUND_ROWS jmm.job_no , jmm.tech_code , jmm.career_code , jmm.man_count , jmm.daily_wage , jmm.request_wage , jmm.comission_fee , jmm.pay_wage from job_manpower jmm
and jmm.job_no like concat('%',#{jobNo},'%')
order by ${srhOrderBy} ${srhOrderAsc}
limit ${startRow} , ${pageRow}



select 	count(*) count  
from 	job_manpower  
where job_no = #{jobNo}  
and tech_code = #{techCode}  
and career_code = #{careerCode}

choose.when:


update job_manpower set  
where job_no = #{jobNo}  
and tech_code = #{techCode}  
and career_code = #{careerCode}

choose.otherwise:


update job_manpower set  
	 man_count = replace(#{manCount},',','')  
	, daily_wage = replace(#{dailyWage},',','')  
	, request_wage = replace(#{requestWage},',','')  
	, comission_fee = replace(#{comissionFee},',','')  
	, pay_wage = replace(#{payWage},',','')  
where job_no = #{jobNo}  
and tech_code = #{techCode}  
and career_code = #{careerCode}

mapper.update:


delete from job_manpower   
where job_no = #{jobNo}  
and tech_code = #{techCode}  
and career_code = #{careerCode}
